import './Card.css'

export default function Card({ card, handleChoice, flipped, disabled }){

    const handleClick = () => {
        if(!disabled){
            handleChoice(card)
        }
    }

    return(
           <div className="card">
           <div className={flipped ? "flipped" : ""}>
                   <img className="front"
                        src={card.src}
                        alt="front image"
                    />
                    <img
                        className="back"
                        src= "/img/cover.jpeg"
                        onClick= {handleClick}
                        alt="card back"
                    />
               </div>
           </div>

    )
}
